import React, { useEffect, useState } from 'react'
import './RowPost.css'
// import axios from '../../axios'
import instance from '../../axios'
import { apiKey, imageUrl } from '../../constants/constants'


function RowPost() {
  const [movies, setMovies] = useState([])
  useEffect(() => {
    instance.get(
      `discover/movie?api_key=${apiKey}&language=en-US&sort_by=popularity.\
      desc&include_video=false&page=1&with_genres=${28}`).then(response=>{
        setMovies(response.data.results)
        console.log(response.data.results)
      })
  }, [])

  return (
    <div className='row'>
        <h1 className='title'>Title</h1>
        <div className="posters">
          {movies.map((obj)=>
          <img className='poster' src={ obj.backdrop_path ? `${imageUrl+obj.backdrop_path}` : `${defau}` } alt="Poster Loading..." />
          )}

        </div>
    </div>
  )
}

export default RowPost
